# https://www.codewars.com/kata/554e4a2f232cdd87d9000038

require 'benchmark'

def DNA_strand(dna)
	complements = {"A" => "T", "T" => "A", "G"=>"C", "C"=>"G"}
	dna.split('').map {|base| complements[base] }.join
end

def DNA_strand_2(dna)
	dna.tr("ACTG", "TGAC")
end

puts Benchmark.measure{DNA_strand_2("")}
puts Benchmark.measure{DNA_strand("")}


